import React , {useState, useEffect}from "react";
import Header from "./Header";
import axios from "axios";
import { ToastContainer, toast,Slide  } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


export default function Login(){
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState("");
    //
    const handleLogin = () => {
        axios.get(`http://localhost:4000/users?username=${username}&password=${password}`)
            .then(response => {
                if (response.data.success === true) {
                    // Hiển thị thông báo khi đăng nhập thành công
                    toast(response.data.message,{
                        position: "top-center",
                        autoClose: 5000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        theme: "light",
                        transition: Slide,
                    });
                    // Sau đó, thực hiện các hành động cần thiết (ví dụ: điều hướng người dùng đến trang chính)
                } else {
                    // Đăng nhập thất bại, hiển thị thông báo cho người dùng
                    toast(response.data.message,{
                        position: "top-center",
                        autoClose: 5000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        theme: "light",
                        transition: Slide,
                    });
                }
            })
            .catch((error) => {
                // Xử lý lỗi khi yêu cầu không thành công
                toast.error("Sai tên tài khoản hoặc mật khẩu", {
                  position: "top-center",
                  autoClose: 5000,
                  hideProgressBar: false,
                  closeOnClick: true,
                  pauseOnHover: true,
                  draggable: true,
                  progress: undefined,
                  theme: "light",
                  transition: Slide,
                });
              });
    };
    //
    return(
        <>
            <Header />
            <div>
                <div>
                    <h2>Đăng nhập</h2>
                    <input
                        type="username"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="Mật khẩu"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button onClick={handleLogin}>Đăng nhập</button>
                </div>
            </div>
            <ToastContainer
                position="top-center"
                autoClose={5000}
                hideProgressBar={false}
                newestOnTop={false}
                closeOnClick
                rtl={false}
                pauseOnFocusLoss
                draggable
                pauseOnHover
                theme="light"
                transition= {Slide}
            />
        </>
    )
}
