import React , {useState, useEffect}from "react";
import Header from "./Header";
import axios from "axios";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


export default function Login(){
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    //
    const handleLogin = () => {
        axios.get(`http://localhost:4000/users?username=${username}&password=${password}`)
            .then(response => {
                if (response.data.success) {
                    // Hiển thị thông báo khi đăng nhập thành công
                    <>
                    toast.success(response.data.message);
                    </>
                    // Sau đó, thực hiện các hành động cần thiết (ví dụ: điều hướng người dùng đến trang chính)
                } else {
                    // Đăng nhập thất bại, hiển thị thông báo cho người dùng
                    console.log(response.data.message);
                }
            })
            .catch(error => {
                console.error(error);
            });
    };
    //
    return(
        <>
            <Header />
            <div>
                <div>
                    <h2>Đăng nhập</h2>
                    <input
                        type="username"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="Mật khẩu"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button onClick={handleLogin}>Đăng nhập</button>
                </div>
            </div>
        </>
    )
}
