import React, {useState} from 'react'
import axios from 'axios';

function Register() {
    
    const [values, setValues] = useState({
        username:'',
        email:'',
        password:''
    })
    const handleSubmit = (event) =>{
        event.preventDefault();
        axios.post('http://localhost:4000/register', values)
        .then(res => console.log(res))
        .then(err => console.log(err));
    }
return (
<div>
  <div>
    <div>
      <h2>Đăng nhập</h2>
      <input type="text" placeholder="Username" onChange={(e) => setValues({...values, name: e.target.value})}
      />
      <input type="email" placeholder="Email" onChange={(e) => setValues({...values, email: e.target.value})}
      />
      <input type="password" placeholder="Mật khẩu" onChange={(e)=> setValues({...values,password:e.target.value})}
      />
      <button tyle="submit" >Đăng nhập</button>
    </div>
  </div>
</div>
)
}

export default Register
